import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';
import { Button } from 'reactstrap';
import logo from '../../assets/images/logo.png'

const BookingDetailsPDF = () => {
    const pdfGenerate = async () => {
        try {
          const response = await fetch(`${BASE_URL}/booking`);
          if (!response.ok) {
            throw new Error("Failed to fetch booking details");
          }
          const bookingDetails = await response.json();

          // Calculate the height needed for the content
          const contentHeight = 90; // Adjust as needed based on the content

          // Set the page size based on the content height
          var doc = new jsPDF({
            orientation: "portrait",
            unit: "mm",
            format: [80, contentHeight]
          });

          // Add the logo at the top
          doc.addImage(logo, "PNG", 25, 5, 30, 30); // Adjusted the size and position of the logo

          // Add the content below the logo
          doc.setFontSize(10); // Reduced font size
          doc.setFont("Helvetica", "bold");
          doc.text(10, 40, "Name:");
          doc.text(10, 45, "Phone:");
          doc.text(10, 50, "User ID:");
          doc.text(10, 55, "Email:");
          doc.text(10, 60, "Time:");

          doc.setFont("Helvetica", "normal");

          doc.text(40, 40, booking.name);
          doc.text(40, 45, booking.phone);
          doc.text(40, 50, booking.userId);
          doc.text(40, 55, booking.userEmail);
          doc.text(40, 55, booking.guestSize);
          doc.text(40, 60, booking.bookAt);

          // Save the PDF
          doc.save("a.pdf");
        } catch (error) {
          console.error("Error fetching booking details:", error);
        }
      };




  return (

      <div style={{textAlign: 'center'}}>
         <Button onClick={pdfGenerate}>Download pdf</Button>
      </div>

  )
};

export default BookingDetailsPDF;
