import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';
import { Button } from 'reactstrap';
import logo from '../../assets/images/logo.png'

const BookingDetailsPDF = () => {
    const pdfGenerate = () => {
        // Calculate the height needed for the content
        const contentHeight = 100; // Adjust as needed based on the content

        // Set the page size based on the content height
        var doc = new jsPDF({
          orientation: 'portrait',
          unit: 'mm',
          format: [80, contentHeight]
        });

        // Add the content
        doc.addImage(logo, 'PNG', 5, 5, 20, 20); // Adjusted the size and position of the logo
        doc.setFontSize(10); // Reduced font size
        doc.setFont('Helvetica', 'bold');
        doc.text(30, 10, 'Full name');
        doc.text(30, 15, 'Email');
        doc.text(30, 20, 'Phone');
        doc.text(30, 25, 'Guest Size');
        doc.text(30, 30, 'Booked At');
        doc.text(30, 35, 'City');

        doc.setFont('Helvetica', 'normal');

        doc.text(60, 10, 'Dare Abidemi');
        doc.text(60, 15, 'dareisrael4');
        doc.text(60, 20, '07030661043');
        doc.text(60, 25, '3');
        doc.text(60, 30, '2-3-2024');
        doc.text(60, 35, 'Lagos');

        // Save the PDF
        doc.save('ticket.pdf');
      };


  return (

      <div style={{textAlign: 'center'}}>
         <Button onClick={pdfGenerate}>Download pdf</Button>
      </div>

  )
};

export default BookingDetailsPDF;
