import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';
import { Button } from 'reactstrap';
import logo from '../../assets/images/logo.png'

const BookingDetailsPDF = () => {
    const pdfGenerate = () => {
        var doc = new jsPDF('landscape', 'px', 'letter');
        doc.addImage(logo, 'PNG', 10, 10, 30, 30); // Adjusted the size and position of the logo
        doc.setFont('Helvetica', 'bold');
        doc.text(60, 60, 'Full name');
        doc.text(60, 80, 'Email');
        doc.text(60, 100, 'Phone');
        doc.text(60, 120, 'Guest Size');
        doc.text(60, 140, 'Booked At');
        doc.text(60, 160, 'City');

        doc.setFont('Helvetica', 'normal');

        doc.text(160, 60, 'Dare Abidemi');
        doc.text(160, 80, 'dareisrael4');
        doc.text(160, 100, '07030661043');
        doc.text(160, 120, '3');
        doc.text(160, 140, '2-3-2024');
        doc.text(160, 160, 'Lagos');

        doc.save('a.pdf');
      };



  return (

      <div style={{textAlign: 'center'}}>
         <Button onClick={pdfGenerate}>Download pdf</Button>
      </div>

  )
};

export default BookingDetailsPDF;
