import React, { useState, useEffect } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';
import { Button } from 'reactstrap';
import logo from '../../assets/images/logo.png';
import { useParams } from 'react-router-dom';
import { useTicketContext } from '../context/useTicketContext';

const BookingDetailsPDF = () => {
    const { id } = useParams();
    // const [booking, setBooking] = useState(null);

    const {bookings, dispatch} = useTicketContext()

    useEffect (() => {
        const fetchPosts = async () => {

            const response = await fetch (`${BASE_URL}/booking`)
            const json = await response.json()

            if (response.ok) {
            dispatch({type: 'SET_TICKETS', payload: json})
                // setPosts(json)

            }
        }

        fetchPosts()
     }, [])

    const pdfGenerate = () => {
        if (!bookings) {
            console.error("Booking details not available");
            return;
        }

        // Generate PDF using booking details
        const doc = new jsPDF();
        doc.text(10, 10, `Full Name: {bookings.fullName}`);
        doc.text(10, 20, `Phone: ${bookings.phone}`);
        doc.text(10, 30, `User ID: ${bookings.userId}`);
        doc.text(10, 40, `Email: ${bookings.userEmail}`);
        doc.text(10, 50, `Guest Size: ${bookings.guestSize}`);
        doc.text(10, 60, `Booked At: ${new Date(bookings.bookAt).toLocaleString()}`);

        // Save the PDF
        doc.save("ticket.pdf");
    };

    return (
        <div style={{ textAlign: 'center' }}>
            {/* <Button onClick={fetchBookingDetails}>Fetch Booking Details</Button> */}
            <br />
            <Button onClick={pdfGenerate}>Download Ticket</Button>
        </div>
    );
};

export default BookingDetailsPDF;
