import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';

const BookingDetailsPDF = () => {
  const [bookingDetails, setBookingDetails] = useState(null);

  const fetchBookingDetails = async () => {
    try {
      const response = await fetch(`${BASE_URL}/booking`);
      if (!response.ok) {
        throw new Error('Failed to fetch booking details');
      }
      const data = await response.json();
      setBookingDetails(data);
    } catch (error) {
      console.error('Error fetching booking details:', error);
    }
  };

  const generatePDF = () => {
    if (!bookingDetails) return;

    const doc = new jsPDF();
    doc.text('Booking Details', 10, 10);

    let yOffset = 20;
    for (const key in bookingDetails) {
      doc.text(`${key}: ${bookingDetails[key]}`, 10, yOffset);
      yOffset += 10;
    }

    doc.save('booking_details.pdf');
  };

  return (
    <div>
      <h2>Booking Details PDF</h2>
      <button onClick={fetchBookingDetails}>Fetch Booking Details</button>
      <button onClick={generatePDF} disabled={!bookingDetails}>Generate PDF</button>
    </div>
  );
};

export default BookingDetailsPDF;
