import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';
import { Button } from 'reactstrap';
import logo from '../../assets/images/logo.png';
import { useParams } from 'react-router-dom';

const BookingDetailsPDF = () => {
    const { id } = useParams();
    const [bookingDetails, setBookingDetails] = useState(null);

    const fetchBookingDetails = async () => {
        try {
            const response = await fetch(`${BASE_URL}/booking`);
            if (!response.ok) {
                throw new Error("Failed to fetch booking details");
            }
            const booking = await response.json();
            setBookingDetails(booking);
        } catch (error) {
            console.error("Error fetching booking details:", error);
        }
    };

    const pdfGenerate = () => {
        if (!bookingDetails) {
            console.error("Booking details not available");
            return;
        }

        // Generate PDF using booking details
        const doc = new jsPDF();
        doc.text(10, 10, `Full Name: ${bookingDetails.fullName}`);
        doc.text(10, 20, `Phone: ${bookingDetails.phone}`);
        doc.text(10, 30, `User ID: ${bookingDetails.userId}`);
        doc.text(10, 40, `Email: ${bookingDetails.userEmail}`);
        doc.text(10, 50, `Guest Size: ${bookingDetails.guestSize}`);
        doc.text(10, 60, `Booked At: ${new Date(bookingDetails.bookAt).toLocaleString()}`);

        // Save the PDF
        doc.save("ticket.pdf");
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <Button onClick={fetchBookingDetails}>Fetch Booking Details</Button>
            <br />
            <Button onClick={pdfGenerate}>Download Ticket</Button>
        </div>
    );
};

export default BookingDetailsPDF;
