import React, {useState, useContext} from 'react'
import { Container, Row, Col, Form, FormGroup, Button } from 'reactstrap'
import { Link, useNavigate } from 'react-router-dom'
import '../../styles/login.css'
import registerImg from '../../assets/images/register.png'
import userIcon from '../../assets/images/user.png'
import { AuthContext } from '../context/AuthContext'

import { BASE_URL } from '../../utils/config'

const Register = () => {

  const [reg, setReg] = useState()

  const [credentials, setCredentials] = useState({
    userName:"",
    email:"",
    password:"",
    phone:0
})

const {dispatch } = useContext (AuthContext)
const navigate = useNavigate()

const handleChange =  e => {
  setCredentials(prev=>({...prev, [e.target.id]:e.target.value}))
}

const handleClick = async e => {
  e.preventDefault();

  try {
    const res = await fetch (`${BASE_URL}/auth/register`, {
      method:'post',
      headers : {
        'content-type' : 'application/json'
      },
      body: JSON.stringify(credentials)
    })
    const result = await res.json()

    if (!res.ok) {
      alert(result.message)
      navigate('/failtoregister');
      return;
    }


    dispatch({type:'REGISTER_SUCCESS'})

    navigate('/successfulregister')





      } catch (err) {

   alert(err.message);
    //  navigate('/failtoregister')
  }
};


  return (
    <section>
     <Container>
      <Row>
        <Col lg='8' className='m-auto'>
        <div className='login__container d-flex justify-content-between'>
        <div className='login__img'>
         <img src={registerImg} alt=''/>
        </div>
        <div className='login__form'>
         <div className='user'>
         <img src={userIcon} alt=''/>
         </div>
         <h2>Register</h2>
         <Form onSubmit={handleClick}>
          <FormGroup>
            <input type='text' placeholder='Username' required id='username' onChange={handleChange}/>
          </FormGroup>
          <FormGroup>
            <input type='email' placeholder='Email' required id='email' onChange={handleChange}/>
          </FormGroup>
          <FormGroup>
            <input type='number' placeholder='Phone' required id='phone' onChange={handleChange}/>
          </FormGroup>
          <FormGroup>
            <input type='password' placeholder='Password' required id='password' onChange={handleChange}/>
          </FormGroup>
          <Button className='btn secondary_btn auth__btn' type='submit'>Create Account</Button>

         </Form>
         <p>Already have an account?<Link to='/login'>Login</Link></p>
        </div>
        </div>
        </Col>
      </Row>
     </Container>
    </section>
  )
}

export default Register
