import React, { useState } from 'react';
import jsPDF from 'jspdf';
import { BASE_URL } from '../../utils/config';
import { Button } from 'reactstrap';
import logo from '../../assets/images/logo.png'

const BookingDetailsPDF = () => {
  const pdfGenerate = () => {
    var doc = new jsPDF ('landscape', 'px', 'a4', 'false')
    doc.addImage(logo, 'PNG', 65,20,60,400)
    doc.setFont('Helvertica', 'bold')
    doc.text(60,60, 'Full name')
    doc.text(60,60, 'Email')
    doc.text(60,100, 'Phone')
    doc.text(60,60, 'Guest Size')
    doc.text(60,100, 'bookAt')
    doc.text(60,60, 'City')

    doc.setFont('Helvertica', 'normal')

    doc.text(60,60, 'Dare Abidemi')
    doc.text(60,60, 'dareisrael4')
    doc.text(60,100, '07030661043')
    doc.text(60,60, 'Guest Size')
    doc.text(60,100, 'bookAt')
    doc.text(60,60, 'City')


    doc.save('a.pdf')
  }


  return (

      <div style={{textAlign: 'center'}}>
         <Button onClick={pdfGenerate}>Download pdf</Button>
      </div>

  )
};

export default BookingDetailsPDF;
